function showView(view) {
  document.querySelectorAll('.view').forEach(v => v.classList.remove('active'));
  document.getElementById('view-' + view).classList.add('active');
}

function toggleTheme() {
  document.body.classList.toggle('dark');
  document.body.classList.toggle('light');
}

const music_list = [
  { img:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMl0AJWrIud4FvgNjhrTXJWlgdUPvfYi5sLw&s', name:'Theri', artist:'G>V>Prakash', music:'music/stay.mp3' },
  { img:'data:image/jpeg;base64,', name:'vaa vaathi', artist:'G>V>Prakash', music:'music/fallingdown.mp3' },
  { img:'data:image/jpeg;base64,', name:'OG Sambavam', artist:'G>V>Prakash', music:'music/OG Sambavam.mp3' },
  { img:'data:image/jpeg;base64,', name:'Anbe Anbe', artist:'G.V.Prakash', music:'music/Rather Be.mp3' }
];

let track_index = 0, isPlaying = false, isRandom = false, updateTimer;
const curr_track = document.createElement('audio');
document.body.appendChild(curr_track);

// DOM elements
const nowPlaying = document.querySelector('.now-playing');
const trackArt = document.querySelector('.track-art');
const trackName = document.querySelector('.track-name');
const trackArtist = document.querySelector('.track-artist');
const playpauseBtn = document.querySelector('.playpause-track');
const prevBtn = document.querySelector('.prev-track');
const nextBtn = document.querySelector('.next-track');
const seekSlider = document.querySelector('.seek_slider');
const volumeSlider = document.querySelector('.volume_slider');
const currTimeEl = document.querySelector('.current-time');
const totalDurationEl = document.querySelector('.total-duration');
const wave = document.getElementById('wave');
const randomIcon = document.querySelector('.fa-random');
const playlistEl = document.getElementById('playlist');

// Build playlist
music_list.forEach((s,i)=>{
  const li = document.createElement('li');
  li.textContent = `${s.name} — ${s.artist}`;
  li.onclick = () => { track_index=i; loadTrack(i); playTrack(); showView('home'); };
  playlistEl.appendChild(li);
});

function loadTrack(i) {
  clearInterval(updateTimer);
  resetValues();
  const s = music_list[i];
  curr_track.src = s.music;
  curr_track.load();
  trackArt.style.backgroundImage = `url(${s.img})`;
  trackName.textContent = s.name;
  trackArtist.textContent = s.artist;
  nowPlaying.textContent = `Playing ${i+1} of ${music_list.length}`;
  highlightList();
  curr_track.addEventListener('ended', nextTrack);
  updateTimer = setInterval(setUpdate, 1000);
  changeBackground();
}

function resetValues() {
  currTimeEl.textContent = '00:00';
  totalDurationEl.textContent = '00:00';
  seekSlider.value = 0;
}

function highlightList() {
  [...playlistEl.children].forEach((li,i)=>{
    li.classList.toggle('active', i===track_index);
  });
}

function changeBackground() {
  const hex='0123456789ABCDEF';
  let c1='#', c2='#';
  for(let i=0;i<6;i++){
    c1+=hex[Math.random()*16|0];
    c2+=hex[Math.random()*16|0];
  }
  document.body.style.background = `linear-gradient(to right,${c1},${c2})`;
}

function playTrack(){
  curr_track.play();
  isPlaying=true;
  playpauseBtn.innerHTML = '<i class="fa fa-pause-circle fa-2x"></i>';
  trackArt.classList.add('rotate');
  wave.style.display = 'flex';
}

function pauseTrack(){
  curr_track.pause();
  isPlaying=false;
  playpauseBtn.innerHTML = '<i class="fa fa-play-circle fa-2x"></i>';
  trackArt.classList.remove('rotate');
  wave.style.display = 'none';
}

function playpauseTrack(){
  if(isPlaying) pauseTrack();
  else playTrack();
}

function nextTrack(){
  track_index = isRandom ?
    Math.floor(Math.random()*music_list.length) :
    (track_index+1) % music_list.length;
  loadTrack(track_index); playTrack();
}

function prevTrack(){
  track_index = isRandom ?
    Math.floor(Math.random()*music_list.length) :
    (track_index-1 + music_list.length) % music_list.length;
  loadTrack(track_index); playTrack();
}

function randomTrack(){
  isRandom = !isRandom;
  randomIcon.classList.toggle('randomActive', isRandom);
}

function repeatTrack(){
  loadTrack(track_index);
  playTrack();
}

seekSlider.oninput = () => {
  if(curr_track.duration)
    curr_track.currentTime = curr_track.duration * (seekSlider.value/100);
};

volumeSlider.oninput = () => {
  curr_track.volume = volumeSlider.value/100;
};

function setUpdate(){
  if(!isNaN(curr_track.duration)){
    const p = curr_track.currentTime * (100 / curr_track.duration);
    seekSlider.value = p;
    currTimeEl.textContent = formatTime(curr_track.currentTime);
    totalDurationEl.textContent = formatTime(curr_track.duration);
  }
}

function formatTime(s){
  const m = Math.floor(s/60);
  const sec = ("0"+Math.floor(s%60)).slice(-2);
  return `${m}:${sec}`;
}

// Initialize
loadTrack(track_index);
showView('home');
